mapper.select:
SELECT a.*
FROM attendance a
INNER JOIN users u ON a.user_id = u.id
WHERE u.id = #{userId}
AND a.date BETWEEN #{startDate} AND #{endDate}

SELECT *
FROM attendance
WHERE user_id = #{userId}
AND date = #{date}

SELECT id, user_id, date, status, hours
FROM attendance
WHERE user_id = #{userId}
AND date BETWEEN #{startDate} AND #{endDate}
